            msg.payload += "We found a flame\n";
        }
        if (temp > 0) {
            msg.payload += `Your temperature is very high: ${temp}°C\n `
        }
        if (gas > 0) {
            msg.payload += `Your gas is very high: ${gas}ppm\n `
        }
        msg.ifttt = msg.payload;
    } else return null;
    return msg;
} catch (error) {
    msg.error = error;
    return msg;
};

